import React from "react";

import "./styles/BadgesList.css";

class BadgesListItem extends React.Component {
  render() {
    return (
      <div className="BadgesListItem">
        <img
          className="BadgesListItem__avatar"
          src={this.props.badge.avatarUrl}
          alt={`${this.props.badge.firstName} ${this.props.badge.lastName}`}
        />

        <div>
          <strong>
            {this.props.badge.firstName} {this.props.badge.lastName}
          </strong>
          
          
          <div className="BadgesList__twitter">
            <figure className="BadgesList__twitter-i">
              <img src="images/twitter-logo.png" alt="twitter"/>
              
            </figure>
            <div className="BadgeList__twitter-acount">@{this.props.badge.twitter}</div>
            
          </div>
          <div><small>{this.props.badge.jobTitle}</small></div>

          
        </div>
      </div>
    );
  }
}

class BadgeLists extends React.Component {
  render () {
    return (
      <div className="Badges__container">
        <div className="BadgesList">
          <ul className="list-unstyled">
            {this.props.badges.map((data) => {
              return (
                <li key={badge.id}>
                  <BadgesListItem badge={data} />
                </li>
              );
            })}
          </ul>
        </div>
      </div>
    );
  }
}

export default BadgeLists;
