import React from "react";

import Navbar from "../components/Navbar";
import Badge from "../components/Badge";
import BadgeForm from "../components/BadgeForm.js";

import header from "../images/astronaut2.svg";
import './styles/BadgeNew.css';

class BadgeNew extends React.Component {
    state = {
        form : {},
        firstName: '',
        lastName: '',
        email: '',
        jobTitle: 'DESIGN_WEB',
        twitter: ''
    };

    handleChange = e => {

        this.setState({
          form: {
            ... this.state.form,  
            [e.target.name]: e.target.value ,
            },
        });
    };

    render() {
        return (
          <React.Fragment>
            
            <div id="slider" className="BadgeNew__hero">
              <img className="animar_astro" src={header} alt="" />
            </div>

            <div className="container">
                <div className="row">
                    <div className="col-6">
                        <Badge
                        firstName={this.state.form.firstName}
                        lastName={this.state.form.lastName}
                        twitter={this.state.form.twitter}
                        job={this.state.form.jobTitle}
                        email={this.state.form.email}
                        avatar="https://pbs.twimg.com/profile_images/1212267729236955136/A2wKmE9d_400x400.jpg"
                        />
                    </div>
                    <div className="col-6"><BadgeForm onChange={this.handleChange}/></div>
                </div>
            </div>
          </React.Fragment>
        );
    }
} 


export default BadgeNew;
